const strategies = {
    isRequired:function(value,errorMsg) {
        if(value === ''){
            return errorMsg;
        }
    },
    maxLength:function(value,length,errorMsg) {//æœ€å°é•¿åº¦
        if(value.length > length){
            return errorMsg;
        }
    },
    minLength:function(value,length,errorMsg) {//æœ€å°é•¿åº¦
        if(value.length < length){
            return errorMsg;
        }
    },
    isPhone:function(value,errorMsg) {
        return this.isReg(value, /^1[3|4|5|7|8]\d{9}$/g, errorMsg);
    },
    isEmail:function(value, errorMsg) {
        return this.isReg(value, /^(\w-*\.*)+@(\w-?)+(\.\w{2,})+$/g, errorMsg);
    },
    isIdCard:function(value, errorMsg) {
        return this.isReg(value, /(^\d{15}$)|(^\d{17}([0-9]|X)$)/g, errorMsg);
    },
    isCar:function(value, errorMsg) {
        return this.isReg(value, /^[\u4e00-\u9fa5]{1}[A-Z]{1}[A-Z_0-9]{5}$/g, errorMsg);
    },
    isReg:function(value,reg,errorMsg) {//æ­£åˆ™åŒ¹é…
        if(!reg.test(value)){
            return errorMsg;
        }
    }
};

export default class Validator {
    constructor() {
        this.errMsg = [];
    }

    validate(value, rules) {
        let flag = true;
        for(let i = 0 ,rule; rule = rules[i++];){
            let strategyAry = rule.strategy.split(':');
            let errorMsg = rule.errorMsg;
            let strategy = strategyAry.shift();
            strategyAry.unshift(value);
            strategyAry.push(errorMsg);
            let err = strategies[strategy].apply(strategies, strategyAry);
            if(err){
                flag = false;
                this.errMsg.push(err);
            }
        }
        return flag;
    }

    validates(source, rules) {
        for (let key of Object.keys(rules)){
            if(source[key]){
                let result = this.validate(source[key], rules[key]);
                if(!result){
                    const firstError = this.errMsg.shift();
                    this.errMsg = [];
                    return firstError;
                }
            }else{
                return rules[key][0].errorMsg;
            }
        }

    }

}

